import { NextRequest } from 'next/server';
import { ok, fail, wrap } from '@/utils/api';
import { assertRateLimit } from '@/middleware/rateLimit';
import { logRequest } from '@/middleware/log';

// 200 ms of silence MP3 (very small) â€“ placeholder for mock mode
// This is a minimal valid MP3 file containing ~200ms of silence
const SILENT_MP3_BASE64 =
  'data:audio/mpeg;base64,';

type Body = { text?: string; lang?: string; voiceId?: string };

export const POST = wrap(async (req: NextRequest) => {
  const t0 = Date.now();
  assertRateLimit(req, '/api/tts');

  const { text, lang = 'de', voiceId } = (await req.json()) as Body;
  const trimmed = (text || '').trim();
  if (!trimmed) return fail(400, 'missing_text');
  if (trimmed.length > 200) return fail(400, 'text_too_long');

  // Mock mode (for demos/CI without secrets)
  if (process.env.NEXT_PUBLIC_ENABLE_MOCK_TTS === 'true') {
    logRequest(req, '/api/tts', Date.now() - t0);
    return ok({ audioBase64: SILENT_MP3_BASE64 });
  }

  if (!process.env.ELEVENLABS_API_KEY) {
    return fail(503, 'tts_unavailable');
  }

  const vid = voiceId || process.env.ELEVENLABS_VOICE_ID || 'EXAVITQu4vr4xnSDxMaL';
  const r = await fetch(`https://api.elevenlabs.io/v1/text-to-speech/${vid}`, {
    method: 'POST',
    headers: {
      'xi-api-key': process.env.ELEVENLABS_API_KEY!,
      'Content-Type': 'application/json',
    },
    // model_id supports multilingual; voice_settings kept modest for stability
    body: JSON.stringify({
      text: trimmed,
      model_id: 'eleven_multilingual_v2',
      voice_settings: { stability: 0.4, similarity_boost: 0.7 },
    }),
  });

  if (!r.ok) {
    // optionally log status/text length for diagnostics
    return fail(502, 'tts_failed');
  }

  const buf = Buffer.from(await r.arrayBuffer());
  const base64 = buf.toString('base64');

  logRequest(req, '/api/tts', Date.now() - t0);
  return ok({ audioBase64: `data:audio/mpeg;base64,${base64}` });
}, '/api/tts');

